package LavPack1;

public class fibonacci {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int n = 5;
	int j=1;
	int i=0;
	
	System.out.println(i);
	System.out.println(j);
	for (int l=0;l<=n;l++)
	{
		 int k = i+j;
		
	System.out.println(k);	
	i=j;
	j=k;
	
	}
	}
	
	

	}


